//    var changed = false
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding = FragmentMainBinding.inflate(inflater)
        binding.lifecycleOwner = this

        binding.viewModel = viewModel


        initRecyclerView()

        viewModel.pictureOfDay.observe(viewLifecycleOwner) {
            if (it != null)
                if (it.mediaType == "image") {
                    binding.activityMainImageOfTheDay.contentDescription = it.title
                    Picasso.get().load(it.url).into(binding.activityMainImageOfTheDay)
                }
        }
//        changed = false
        viewModel.getStarterAsteroidsFromRoom().observe(viewLifecycleOwner) {
            w("NOO", "OH NOO${it.isNullOrEmpty()}")
//            if (!changed) {
                if (it.isNullOrEmpty()) {
                    viewModel.viewModelScope.launch {
                        w("NOO","getAsteroidsFromNetwork")
                        viewModel.getAsteroidsFromNetwork()
                    }
                } else {
                    w("NOO","getStarterAsteroidsFromRoom")
                    adapter.setData(it)
                    adapter.notifyDataSetChanged()
//                    changed = true
                }
//            }
        }

        setHasOptionsMenu(true)

        binding.executePendingBindings()
        return binding.root
    }


    private fun initRecyclerView() {
        val asteroidsList = ArrayList<Asteroid>()
        binding.asteroidRecycler.layoutManager = LinearLayoutManager(context)
        adapter = AsteroidsAdapter(asteroidsList, AsteroidsAdapter.AsteroidListener {
            Navigation.findNavController(binding.root)
                .navigate(
                    MainFragmentDirections.actionShowDetail(it)
                )
        })
        binding.asteroidRecycler.adapter = adapter

    }

    override fun onCreateOptionsMenu(menu: Menu, inflater: MenuInflater) {
        inflater.inflate(R.menu.main_overflow_menu, menu)
        super.onCreateOptionsMenu(menu, inflater)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        val asteroids = when (item.itemId) {
//            R.id.next_week_asteroids -> viewModel.getWeekAsteroidsFromRoom()
            R.id.today_asteroids -> viewModel.getTodayAsteroidsFromRoom()
            else -> viewModel.getSavedAsteroidsFromRoom()
        }

        asteroids.observe(viewLifecycleOwner) {
            adapter.setData(it)
            adapter.notifyDataSetChanged()
        }

        return true
    }
}
